Graph.CDATA:
using System;
using System.Collections;
using System.Collections.Generic;
using PX.SM;
using PX.Data;
using PX.Commerce.BigCommerce;
using PX.Commerce.Core;

namespace DummyAttribute
{
  public class DummyAttributeGraph : PXGraphExtension<BCTemplateItemProcessor>
  {
    [PXOverride]
    public  List<string> GetDummyAttributes()
    {
      List<string> attributes = new List<string>();
      BCDummyAttributeExt bCDummyBindingExt =  Base.GetBindingExt<BCDummyAttributeExt>();
      if(!string.IsNullOrEmpty(bCDummyBindingExt.DummyAttribute))
      {
        var dummies = bCDummyBindingExt.DummyAttribute.Split(new char[] { ','});
        if (dummies != null)
          attributes.AddRange(dummies);
      }
      return attributes;
    }
  }
}
